INSERT INTO `books` (`id`, `img`, `title`, `synopsis`, `id_autor`) VALUES
(21, 'https://books.google.fr/books/content?id=lEnrxE2FUFAC&hl=fr&pg=PP1&img=1&zoom=3&bul=1&sig=ACfU3U213jKJOSDhuiOGtsaWM51KwKufqA&w=1280', 'Hunger Games', 'Hunger Games est le premier tome de la trilogie homonyme de Suzanne Collins, paru en 2008. Il est narré par la jeune Katniss, 16 ans, qui vit dans le district 12, dans la nation post-apocalyptique de Panem, construite sur les ruines de ce qui constituait l\'Amérique du Nord.', 1),
(22, 'data:image/jpg;base64,', 'Hunger Games : L\'embrasement', 'Hunger Games : L\'Embrasement est le deuxième tome de la trilogie The Hunger Games de Suzanne Collins, paru en 2009. Suite directe du premier roman, il continue l\'histoire de Katniss dans la nation post-apocalyptique de Panem. ', 1),
(23, 'data:image/jpg;base64,', 'Hunger Games : La Révolte', 'Hunger Games : La Révolte est le troisième tome de la trilogie The Hunger Games de Suzanne Collins, paru en 2010. Dernier roman de la série, il clôt l\'histoire de Katniss. Les rebelles et elle entreprennent une révolution sanglante contre le Capitole, qui a emprisonné Peeta après la fin des derniers Hunger Games. ', 1),
(24, 'data:image/jpg;base64,', 'Hunger Games : La Ballade du serpent et de l\'oiseau chanteur', 'Hunger Games : La Ballade du serpent et de l\'oiseau chanteur est un préquel de la trilogie Hunger Games, écrit par Suzanne Collins et publié en mai 2020. Préquel des événements de la trilogie Hunger Games, le roman raconte la jeunesse de Coriolanus Snow, dictateur emblématique de Panem.', 1),
(25, 'data:image/jpeg;base64,', 'Harry Potter à l\'école des sorciers', 'Harry Potter à l\'école des sorciers est le premier roman de la série littéraire centrée sur le personnage de Harry Potter, créé par J. K. Rowling. Sorti le 26 juin 1997, il est initialement tiré à 500 exemplaires. En France, le roman a été publié le 9 octobre 1998.', 2);
INSERT INTO `books` (`id`, `img`, `title`, `synopsis`, `id_autor`) VALUES
(26, 'data:image/jpeg;base64,', 'Harry Potter et la Chambre des secrets', 'Harry Potter et la Chambre des secrets est le deuxième roman de la série littéraire centrée sur le personnage de Harry Potter créé par J. K. Rowling. Il a été publié le 2 juillet 1998 par Bloomsbury et le 23 mars 1999 en France.', 2),
(27, 'data:image/jpeg;base64,', 'Harry Potter et le Prisonnier d\'Azkaban', 'Harry Potter et le Prisonnier d\'Azkaban est le troisième roman de la série littéraire centrée sur le personnage de Harry Potter créé par J. K. Rowling. Il a été publié le 19 octobre 1999 en France.', 2),
(28, 'data:image/jpg;base64,', 'Harry Potter et la Coupe de feu', 'Harry Potter a quatorze ans et entre en quatrième année au collège de Poudlard. Une grande nouvelle attend Harry, Ron et Hermione à leur arrivée : la tenue d’un tournoi de magie exceptionnel entre les plus célèbres écoles de sorcellerie. Déjà les délégations étrangères font leur entrée. Harry se réjouit... Trop vite.', 2);
INSERT INTO `books` (`id`, `img`, `title`, `synopsis`, `id_autor`) VALUES
(29, 'data:image/jpg;base64,', 'Harry Potter et l\'Ordre du Phénix', 'À quinze ans, Harry entre en cinquième année à Poudlard, mais il n\'a jamais été si anxieux. L\'adolescence, la perspective des examens et ces étranges cauchemars... Car Celui-Dont-On-Ne-Doit-Pas-Prononcer-Le-Nom est de retour. Le ministère de la Magie semble ne pas prendre cette menace au sérieux, contrairement à Dumbledore. La résistance s\'organise alors autour de Harry qui va devoir compter sur le courage et la fidélité de ses amis de toujours...', 2),
(30, 'data:image/jpeg;base64,', 'Harry Potter et le Prince de sang-mêlé', 'Harry Potter et le Prince de sang-mêlé est le sixième roman de la série littéraire centrée sur le personnage de Harry Potter créé par J. K. Rowling. La version anglaise est sortie simultanément, le 16 juillet 2005, à minuit, dans un grand nombre de pays anglophones.', 2),
(31, 'data:image/jpeg;base64,', 'Harry Potter : Harry Potter et les Reliques de la Mort', 'Cette année, Harry a dix-sept ans et ne retourne pas à Poudlard. Avec Ron et Hermione, il se consacre à la dernière mission confiée par Dumbledore. Mais le Seigneur des Ténèbres règne en maître. Traqués, les trois fidèles amis sont contraints à la clandestinité. D\'épreuves en révélations, le courage, les choix et les sacrifices de Harry seront déterminants dans la lutte contre les forces du Mal.', 2);
INSERT INTO `books` (`id`, `img`, `title`, `synopsis`, `id_autor`) VALUES
(32, 'data:image/jpeg;base64,', ' Roméo et Juliette', '\r\nRoméo et Juliette est une tragédie de William Shakespeare. Écrite vers le début de sa carrière, elle raconte l\'histoire de deux jeunes gens, Roméo Montaigu et Juliette Capulet, qui s\'aiment malgré la haine que se vouent leurs familles et connaissent un destin funeste.', 3),
(33, 'data:image/jpeg;base64,', 'Le Malade Imaginaire', 'Le Malade imaginaire, dernière œuvre dramatique écrite par Molière, est une comédie-ballet en trois actes et en prose, créée le 10 février 1673 par la Troupe du Roi sur la scène du Palais-Royal à Paris, avec une musique de scène composée par Marc-Antoine Charpentier et des ballets réglés par Pierre Beauchamp.', 4);

